import React, { useState, useEffect } from 'react';
import { menuAPI } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';

const MenuManagement = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category_id: '',
    preparation_time: 15,
    is_available: true,
    image: null,
    image_preview: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [menuResponse, categoriesResponse] = await Promise.all([
        menuAPI.getAll(),
        menuAPI.getCategories()
      ]);

      setMenuItems(menuResponse.data.data);
      setCategories(categoriesResponse.data.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validasi tipe file
      const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp'];
      if (!validTypes.includes(file.type)) {
        setError('Please select a valid image file (JPEG, PNG, WebP)');
        return;
      }

      // Validasi ukuran file (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setError('Image size should be less than 5MB');
        return;
      }

      setFormData(prev => ({
        ...prev,
        image: file,
        image_preview: URL.createObjectURL(file)
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setError(null);
      setUploading(true);

      // Prepare form data for file upload
      const submitFormData = new FormData();
      submitFormData.append('name', formData.name);
      submitFormData.append('description', formData.description);
      submitFormData.append('price', parseFloat(formData.price));
      submitFormData.append('category_id', parseInt(formData.category_id));
      submitFormData.append('preparation_time', parseInt(formData.preparation_time));
      submitFormData.append('is_available', formData.is_available);

      // Append image if exists
      if (formData.image) {
        submitFormData.append('image', formData.image);
      }

      if (editingItem) {
        // Update existing item
        await menuAPI.update(editingItem.id, submitFormData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
      } else {
        // Create new item
        await menuAPI.create(submitFormData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
      }
      
      await fetchData(); // Refresh data
      resetForm();
      
    } catch (err) {
      console.error('Error saving menu item:', err);
      setError(err.response?.data?.message || 'Failed to save menu item');
    } finally {
      setUploading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '',
      category_id: '',
      preparation_time: 15,
      is_available: true,
      image: null,
      image_preview: ''
    });
    setEditingItem(null);
    setShowForm(false);
  };

  const handleEdit = (item) => {
    setEditingItem(item);
    setFormData({
      name: item.name,
      description: item.description || '',
      price: item.price.toString(),
      category_id: item.category_id?.toString() || '',
      preparation_time: item.preparation_time || 15,
      is_available: item.is_available,
      image: null,
      image_preview: item.image || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this menu item?')) {
      try {
        await menuAPI.delete(id);
        await fetchData(); // Refresh data
      } catch (err) {
        console.error('Error deleting menu item:', err);
        setError('Failed to delete menu item');
      }
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      image: null,
      image_preview: ''
    }));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  if (loading) {
    return <LoadingSpinner text="Loading menu..." />;
  }

  return (
    <div>
      {/* Page Header */}
      <div className="card-header">
        <h1>Menu Management</h1>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          âž• Add Menu Item
        </button>
      </div>

      {error && (
        <div className="card" style={{ borderLeft: '4px solid #e74c3c' }}>
          <div style={{ color: '#e74c3c', padding: '1rem' }}>
            <strong>Error:</strong> {error}
            <button 
              onClick={() => setError(null)}
              style={{ 
                marginLeft: '1rem',
                background: 'none',
                border: 'none',
                color: '#e74c3c',
                cursor: 'pointer',
                fontSize: '1.2rem'
              }}
            >
              Ã—
            </button>
          </div>
        </div>
      )}

      {/* Menu Form Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="card-header">
              <h2>{editingItem ? 'Edit Menu Item' : 'Add New Menu Item'}</h2>
              <button 
                onClick={resetForm}
                className="btn btn-secondary btn-sm"
              >
                âœ• Close
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              {/* Image Upload Section */}
              <div className="form-group">
                <label className="form-label">Item Image</label>
                <div style={{ display: 'flex', gap: '1rem', alignItems: 'flex-start' }}>
                  {/* Image Preview */}
                  {(formData.image_preview || formData.image) && (
                    <div style={{ position: 'relative', display: 'inline-block' }}>
                      <img 
                        src={formData.image_preview} 
                        alt="Preview" 
                        style={{
                          width: '120px',
                          height: '120px',
                          objectFit: 'cover',
                          borderRadius: '8px',
                          border: '2px solid #e9ecef'
                        }}
                      />
                      <button
                        type="button"
                        onClick={removeImage}
                        style={{
                          position: 'absolute',
                          top: '-8px',
                          right: '-8px',
                          background: '#e74c3c',
                          color: 'white',
                          border: 'none',
                          borderRadius: '50%',
                          width: '24px',
                          height: '24px',
                          cursor: 'pointer',
                          fontSize: '12px',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center'
                        }}
                        title="Remove image"
                      >
                        Ã—
                      </button>
                    </div>
                  )}
                  
                  {/* Upload Area */}
                  <div style={{ flex: 1 }}>
                    <div 
                      style={{
                        border: '2px dashed #bdc3c7',
                        borderRadius: '8px',
                        padding: '1.5rem',
                        textAlign: 'center',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        background: '#f8f9fa'
                      }}
                      onClick={() => document.getElementById('image-upload').click()}
                      onDragOver={(e) => {
                        e.preventDefault();
                        e.currentTarget.style.borderColor = '#3498db';
                        e.currentTarget.style.background = '#ecf0f1';
                      }}
                      onDragLeave={(e) => {
                        e.preventDefault();
                        e.currentTarget.style.borderColor = '#bdc3c7';
                        e.currentTarget.style.background = '#f8f9fa';
                      }}
                      onDrop={(e) => {
                        e.preventDefault();
                        const files = e.dataTransfer.files;
                        if (files.length > 0) {
                          handleImageChange({ target: { files } });
                        }
                        e.currentTarget.style.borderColor = '#bdc3c7';
                        e.currentTarget.style.background = '#f8f9fa';
                      }}
                    >
                      <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>ðŸ“·</div>
                      <div style={{ fontWeight: '600', color: '#2c3e50', marginBottom: '0.25rem' }}>
                        {formData.image_preview ? 'Change Image' : 'Upload Image'}
                      </div>
                      <div style={{ fontSize: '0.8rem', color: '#7f8c8d' }}>
                        Click to browse or drag and drop
                      </div>
                      <div style={{ fontSize: '0.7rem', color: '#95a5a6', marginTop: '0.5rem' }}>
                        PNG, JPG, WebP (Max 5MB)
                      </div>
                    </div>
                    <input
                      id="image-upload"
                      type="file"
                      accept="image/jpeg,image/jpg,image/png,image/webp"
                      onChange={handleImageChange}
                      style={{ display: 'none' }}
                    />
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Item Name *</label>
                <input
                  type="text"
                  className="form-control"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  placeholder="Enter menu item name"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  rows="3"
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  placeholder="Enter item description"
                />
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                <div className="form-group">
                  <label className="form-label">Price (IDR) *</label>
                  <input
                    type="number"
                    className="form-control"
                    value={formData.price}
                    onChange={(e) => setFormData({...formData, price: e.target.value})}
                    placeholder="0"
                    min="0"
                    step="1000"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Preparation Time (minutes)</label>
                  <input
                    type="number"
                    className="form-control"
                    value={formData.preparation_time}
                    onChange={(e) => setFormData({...formData, preparation_time: e.target.value})}
                    min="1"
                    max="120"
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Category *</label>
                <select
                  className="form-control"
                  value={formData.category_id}
                  onChange={(e) => setFormData({...formData, category_id: e.target.value})}
                  required
                >
                  <option value="">Select Category</option>
                  {categories.map(category => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <input
                    type="checkbox"
                    checked={formData.is_available}
                    onChange={(e) => setFormData({...formData, is_available: e.target.checked})}
                  />
                  Available for ordering
                </label>
              </div>

              <div style={{ display: 'flex', gap: '1rem', marginTop: '2rem' }}>
                <button 
                  type="submit" 
                  className="btn btn-primary" 
                  style={{ flex: 1 }}
                  disabled={uploading}
                >
                  {uploading ? (
                    <>
                      <div className="spinner-small"></div>
                      {editingItem ? 'Updating...' : 'Creating...'}
                    </>
                  ) : (
                    <>
                      {editingItem ? 'ðŸ’¾ Update Item' : 'âž• Create Item'}
                    </>
                  )}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={resetForm}
                  disabled={uploading}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Menu Items Table */}
      <div className="card">
        <div className="card-header">
          <h2>Menu Items ({menuItems.length})</h2>
          <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
            <span style={{ 
              padding: '0.25rem 0.75rem', 
              background: '#27ae60', 
              color: 'white',
              borderRadius: '12px',
              fontSize: '0.8rem',
              fontWeight: '500'
            }}>
              Available: {menuItems.filter(item => item.is_available).length}
            </span>
          </div>
        </div>

        {menuItems.length > 0 ? (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th style={{ width: '80px' }}>Image</th>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Prep Time</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {menuItems.map((item) => (
                  <tr key={item.id}>
                    <td>
                      <div style={{
                        width: '60px',
                        height: '60px',
                        borderRadius: '8px',
                        overflow: 'hidden',
                        background: '#f8f9fa',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}>
                        {item.image ? (
                          <img 
                            src={item.image} 
                            alt={item.name}
                            style={{
                              width: '100%',
                              height: '100%',
                              objectFit: 'cover'
                            }}
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                        ) : (
                          <div style={{
                            width: '100%',
                            height: '100%',
                            background: '#ecf0f1',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            color: '#7f8c8d',
                            fontSize: '1.5rem'
                          }}>
                            ðŸ½ï¸
                          </div>
                        )}
                        {item.image && (
                          <div 
                            style={{
                              display: 'none',
                              width: '100%',
                              height: '100%',
                              background: '#ecf0f1',
                              alignItems: 'center',
                              justifyContent: 'center',
                              color: '#7f8c8d',
                              fontSize: '1.5rem'
                            }}
                          >
                            ðŸ½ï¸
                          </div>
                        )}
                      </div>
                    </td>
                    <td>
                      <div>
                        <div style={{ fontWeight: '600', color: '#2c3e50' }}>
                          {item.name}
                        </div>
                        {item.description && (
                          <div style={{ 
                            fontSize: '0.8rem', 
                            color: '#7f8c8d',
                            marginTop: '0.25rem'
                          }}>
                            {item.description.length > 60 
                              ? `${item.description.substring(0, 60)}...`
                              : item.description
                            }
                          </div>
                        )}
                      </div>
                    </td>
                    <td>
                      <span style={{ 
                        background: '#ecf0f1',
                        padding: '0.25rem 0.75rem',
                        borderRadius: '12px',
                        fontSize: '0.8rem',
                        color: '#2c3e50'
                      }}>
                        {item.category_name}
                      </span>
                    </td>
                    <td style={{ fontWeight: '600', color: '#e74c3c' }}>
                      {formatCurrency(item.price)}
                    </td>
                    <td>
                      <span style={{ color: '#7f8c8d' }}>
                        {item.preparation_time} min
                      </span>
                    </td>
                    <td>
                      <span className={`status-badge ${item.is_available ? 'status-completed' : 'status-cancelled'}`}>
                        {item.is_available ? 'Available' : 'Unavailable'}
                      </span>
                    </td>
                    <td>
                      <div style={{ display: 'flex', gap: '0.5rem' }}>
                        <button 
                          className="btn btn-warning btn-sm"
                          onClick={() => handleEdit(item)}
                          title="Edit item"
                        >
                          âœï¸ Edit
                        </button>
                        <button 
                          className="btn btn-danger btn-sm"
                          onClick={() => handleDelete(item.id)}
                          title="Delete item"
                        >
                          ðŸ—‘ï¸ Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '3rem', color: '#7f8c8d' }}>
            <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ðŸ½ï¸</div>
            <h3>No Menu Items</h3>
            <p>Get started by adding your first menu item.</p>
            <button 
              className="btn btn-primary"
              onClick={() => setShowForm(true)}
              style={{ marginTop: '1rem' }}
            >
              âž• Add First Item
            </button>
          </div>
        )}
      </div>

      {/* Add CSS for spinner */}
      <style jsx>{`
        .spinner-small {
          width: 16px;
          height: 16px;
          border: 2px solid transparent;
          border-top: 2px solid white;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          display: inline-block;
          margin-right: 8px;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

// Modal overlay styles (add to App.css)
const modalStyles = `
  .modal-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0,0,0,0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    padding: 2rem;
  }

  .modal-content {
    background: white;
    border-radius: 12px;
    width: 100%;
    max-width: 700px;
    max-height: 90vh;
    overflow-y: auto;
    box-shadow: 0 20px 40px rgba(0,0,0,0.3);
  }

  .form-group {
    margin-bottom: 1.5rem;
  }

  .form-label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 600;
    color: #2c3e50;
  }

  .form-control {
    width: 100%;
    padding: 0.75rem;
    border: 2px solid #e9ecef;
    border-radius: 8px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
  }

  .form-control:focus {
    border-color: #3498db;
    outline: none;
  }

  .btn {
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
  }

  .btn-primary {
    background: #3498db;
    color: white;
  }

  .btn-primary:hover:not(:disabled) {
    background: #2980b9;
    transform: translateY(-2px);
  }

  .btn-secondary {
    background: #95a5a6;
    color: white;
  }

  .btn-secondary:hover:not(:disabled) {
    background: #7f8c8d;
  }

  .btn-warning {
    background: #f39c12;
    color: white;
  }

  .btn-danger {
    background: #e74c3c;
    color: white;
  }

  .btn-sm {
    padding: 0.5rem 1rem;
    font-size: 0.8rem;
  }

  .btn:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none !important;
  }
`;

// Add styles to document
if (!document.querySelector('#modal-styles')) {
  const styleSheet = document.createElement("style");
  styleSheet.id = "modal-styles";
  styleSheet.innerText = modalStyles;
  document.head.appendChild(styleSheet);
}

export default MenuManagement;
