import React, { useState, useEffect } from 'react';
import { dashboardAPI, ordersAPI } from '../../services/api';
import LoadingSpinner from '../components/LoadingSpinner';

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [recentOrders, setRecentOrders] = useState([]);
  const [realTimeOrders, setRealTimeOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchDashboardData();
    const interval = setInterval(fetchRealTimeOrders, 15000); // Refresh every 15 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [statsResponse, ordersResponse, realTimeResponse] = await Promise.all([
        dashboardAPI.getStats(),
        ordersAPI.getAll(),
        dashboardAPI.getRealTimeOrders()
      ]);

      setStats(statsResponse.data.data);
      setRecentOrders(ordersResponse.data.data.slice(0, 8));
      setRealTimeOrders(realTimeResponse.data.data);
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fetchRealTimeOrders = async () => {
    try {
      const response = await dashboardAPI.getRealTimeOrders();
      setRealTimeOrders(response.data.data);
    } catch (err) {
      console.error('Error fetching real-time orders:', err);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', label: 'Pending', icon: 'â³' },
      confirmed: { class: 'status-confirmed', label: 'Confirmed', icon: 'âœ…' },
      preparing: { class: 'status-preparing', label: 'Preparing', icon: 'ğŸ‘¨â€ğŸ³' },
      ready: { class: 'status-ready', label: 'Ready', icon: 'ğŸ¯' },
      completed: { class: 'status-completed', label: 'Completed', icon: 'ğŸ“¦' },
      cancelled: { class: 'status-cancelled', label: 'Cancelled', icon: 'âŒ' }
    };

    const config = statusConfig[status] || statusConfig.pending;
    
    return (
      <span className={`status-badge ${config.class}`}>
        {config.icon} {config.label}
      </span>
    );
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString('id-ID', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return <LoadingSpinner text="Loading dashboard..." />;
  }

  if (error) {
    return (
      <div className="card">
        <div style={{ textAlign: 'center', padding: '2rem', color: '#e74c3c' }}>
          <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>âŒ</div>
          <h3>Error Loading Dashboard</h3>
          <p>{error}</p>
          <button 
            className="btn btn-primary" 
            onClick={fetchDashboardData}
            style={{ marginTop: '1rem' }}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Page Header */}
      <div className="card-header">
        <h1>Dashboard Overview</h1>
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <span style={{ color: '#7f8c8d', fontSize: '0.9rem' }}>
            Last updated: {new Date().toLocaleTimeString()}
          </span>
          <button 
            className="btn btn-primary"
            onClick={fetchDashboardData}
            disabled={loading}
          >
            ğŸ”„ Refresh
          </button>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="card">
        <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '1px solid #e9ecef', paddingBottom: '1rem' }}>
          {[
            { key: 'overview', label: 'ğŸ“Š Overview', icon: 'ğŸ“Š' },
            { key: 'realtime', label: 'âš¡ Real-Time', icon: 'âš¡' },
            { key: 'analytics', label: 'ğŸ“ˆ Analytics', icon: 'ğŸ“ˆ' }
          ].map(({ key, label, icon }) => (
            <button
              key={key}
              className={`btn ${activeTab === key ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setActiveTab(key)}
            >
              {icon} {label}
            </button>
          ))}
        </div>
      </div>

      {activeTab === 'overview' && (
        <>
          {/* Statistics Cards */}
          <div className="stats-grid">
            <div className="stat-card" style={{ borderLeftColor: '#3498db' }}>
              <h3>Total Orders</h3>
              <div className="number">{stats?.totalOrders || 0}</div>
              <div className="trend">
                ğŸ“ˆ Today: {stats?.todayOrders || 0}
              </div>
            </div>
            
            <div className="stat-card" style={{ borderLeftColor: '#27ae60' }}>
              <h3>Total Revenue</h3>
              <div className="number">{formatCurrency(stats?.totalRevenue || 0)}</div>
              <div className="trend">
                ğŸ’° Today: {formatCurrency(stats?.todayRevenue || 0)}
              </div>
            </div>
            
            <div className="stat-card" style={{ borderLeftColor: '#e74c3c' }}>
              <h3>Active Orders</h3>
              <div className="number">
                {stats?.ordersByStatus?.filter(s => 
                  ['pending', 'confirmed', 'preparing', 'ready'].includes(s.status)
                ).reduce((sum, status) => sum + parseInt(status.count), 0) || 0}
              </div>
              <div className="trend">
                âš¡ Needs Attention
              </div>
            </div>

            <div className="stat-card" style={{ borderLeftColor: '#f39c12' }}>
              <h3>Avg Order Value</h3>
              <div className="number">{formatCurrency(stats?.avgOrderValue || 0)}</div>
              <div className="trend">
                ğŸ¯ Completion: {stats?.completionRate || 0}%
              </div>
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '2rem' }}>
            {/* Recent Orders */}
            <div className="card">
              <div className="card-header">
                <h2>Recent Orders</h2>
                <span style={{ color: '#7f8c8d', fontSize: '0.9rem' }}>
                  Last {recentOrders.length} orders
                </span>
              </div>
              
              {recentOrders.length > 0 ? (
                <div className="table-container">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>Order #</th>
                        <th>Customer</th>
                        <th>Amount</th>
                        <th>Status</th>
                        <th>Time</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentOrders.map((order) => (
                        <tr key={order.id}>
                          <td>
                            <strong>{order.order_number}</strong>
                          </td>
                          <td>
                            <div>
                              <div style={{ fontWeight: '600' }}>{order.customer_name}</div>
                              <div style={{ fontSize: '0.8rem', color: '#7f8c8d' }}>
                                {order.table_number || 'Takeaway'}
                              </div>
                            </div>
                          </td>
                          <td style={{ fontWeight: '600' }}>
                            {formatCurrency(order.total_amount)}
                          </td>
                          <td>
                            {getStatusBadge(order.status)}
                          </td>
                          <td style={{ fontSize: '0.85rem', color: '#7f8c8d' }}>
                            {formatTime(order.created_at)}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div style={{ textAlign: 'center', padding: '3rem', color: '#7f8c8d' }}>
                  <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ğŸ“‹</div>
                  <h3>No Orders Yet</h3>
                  <p>Orders will appear here once customers start ordering.</p>
                </div>
              )}
            </div>

            {/* Order Status & Popular Items */}
            <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
              {/* Order Status Summary */}
              <div className="card">
                <div className="card-header">
                  <h2>Order Status</h2>
                </div>
                
                {stats?.ordersByStatus && stats.ordersByStatus.length > 0 ? (
                  <div style={{ padding: '1rem 0' }}>
                    {stats.ordersByStatus.map((status) => (
                      <div 
                        key={status.status}
                        style={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                          padding: '0.75rem 0',
                          borderBottom: '1px solid #e9ecef'
                        }}
                      >
                        <span>{getStatusBadge(status.status)}</span>
                        <span style={{ 
                          fontWeight: 'bold',
                          fontSize: '1.1rem',
                          color: '#2c3e50'
                        }}>
                          {status.count}
                        </span>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div style={{ textAlign: 'center', padding: '2rem', color: '#7f8c8d' }}>
                    No order data available
                  </div>
                )}
              </div>

              {/* Popular Items */}
              {stats?.popularItems && stats.popularItems.length > 0 && (
                <div className="card">
                  <div className="card-header">
                    <h2>Popular Items</h2>
                  </div>
                  <div style={{ padding: '1rem 0' }}>
                    {stats.popularItems.slice(0, 5).map((item, index) => (
                      <div 
                        key={item.id}
                        style={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                          padding: '0.5rem 0',
                          borderBottom: '1px solid #f8f9fa'
                        }}
                      >
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                          <span style={{ 
                            background: '#3498db',
                            color: 'white',
                            borderRadius: '50%',
                            width: '24px',
                            height: '24px',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            fontSize: '0.7rem',
                            fontWeight: 'bold'
                          }}>
                            {index + 1}
                          </span>
                          <span style={{ fontSize: '0.9rem' }}>{item.name}</span>
                        </div>
                        <span style={{ 
                          fontSize: '0.8rem',
                          color: '#7f8c8d'
                        }}>
                          {item.total_quantity}x
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </>
      )}

      {activeTab === 'realtime' && (
        <div className="card">
          <div className="card-header">
            <h2>âš¡ Real-Time Orders</h2>
            <span style={{ color: '#7f8c8d', fontSize: '0.9rem' }}>
              Auto-refresh every 15 seconds
            </span>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem', padding: '1.5rem' }}>
            {realTimeOrders.map((statusGroup) => (
              <div key={statusGroup.status} className="card">
                <div className="card-header">
                  <h3 style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    {getStatusBadge(statusGroup.status)}
                    <span style={{ 
                      background: '#e74c3c',
                      color: 'white',
                      borderRadius: '50%',
                      width: '24px',
                      height: '24px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '0.8rem'
                    }}>
                      {statusGroup.count}
                    </span>
                  </h3>
                </div>
                
                <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                  {statusGroup.orders && statusGroup.orders.length > 0 ? (
                    statusGroup.orders.map((order) => (
                      <div 
                        key={order.id}
                        style={{
                          padding: '1rem',
                          borderBottom: '1px solid #e9ecef',
                          background: '#f8f9fa',
                          marginBottom: '0.5rem',
                          borderRadius: '8px'
                        }}
                      >
                        <div style={{ fontWeight: '600', color: '#2c3e50' }}>
                          {order.order_number}
                        </div>
                        <div style={{ fontSize: '0.9rem', color: '#7f8c8d' }}>
                          {order.customer_name} â€¢ {order.table_number || 'Takeaway'}
                        </div>
                        <div style={{ fontSize: '0.9rem', fontWeight: '500', color: '#e74c3c' }}>
                          {formatCurrency(order.total_amount)}
                        </div>
                        <div style={{ fontSize: '0.8rem', color: '#95a5a6' }}>
                          {formatTime(order.created_at)}
                        </div>
                        {order.items && order.items.length > 0 && (
                          <div style={{ marginTop: '0.5rem', fontSize: '0.8rem' }}>
                            {order.items.slice(0, 2).map((item, idx) => (
                              <div key={idx}>
                                {item.quantity}x {item.menu_item_name}
                              </div>
                            ))}
                            {order.items.length > 2 && (
                              <div style={{ color: '#7f8c8d' }}>
                                +{order.items.length - 2} more items
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    ))
                  ) : (
                    <div style={{ textAlign: 'center', padding: '2rem', color: '#7f8c8d' }}>
                      No orders in this status
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'analytics' && stats && (
        <div style={{ display: 'grid', gap: '2rem' }}>
          {/* Weekly Revenue */}
          {stats.weeklyRevenue && stats.weeklyRevenue.length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2>ğŸ“ˆ Weekly Revenue</h2>
              </div>
              <div style={{ padding: '1.5rem' }}>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1rem' }}>
                  {stats.weeklyRevenue.map((day) => (
                    <div key={day.date} style={{ textAlign: 'center' }}>
                      <div style={{ fontSize: '0.8rem', color: '#7f8c8d' }}>
                        {new Date(day.date).toLocaleDateString('id-ID', { weekday: 'short' })}
                      </div>
                      <div style={{ fontWeight: 'bold', color: '#2c3e50', margin: '0.5rem 0' }}>
                        {formatCurrency(day.revenue)}
                      </div>
                      <div style={{ fontSize: '0.7rem', color: '#95a5a6' }}>
                        {day.order_count} orders
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Popular Items Detailed */}
          {stats.popularItems && stats.popularItems.length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2>ğŸ† Top Performing Menu Items</h2>
              </div>
              <div className="table-container">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Menu Item</th>
                      <th>Orders</th>
                      <th>Quantity</th>
                      <th>Revenue</th>
                      <th>Performance</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.popularItems.map((item, index) => (
                      <tr key={item.id}>
                        <td>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <span style={{ 
                              background: index < 3 ? '#f39c12' : '#95a5a6',
                              color: 'white',
                              borderRadius: '4px',
                              padding: '0.25rem 0.5rem',
                              fontSize: '0.7rem',
                              fontWeight: 'bold'
                            }}>
                              #{index + 1}
                            </span>
                            {item.name}
                          </div>
                        </td>
                        <td>{item.order_count}</td>
                        <td>{item.total_quantity}</td>
                        <td style={{ fontWeight: '600', color: '#27ae60' }}>
                          {formatCurrency(item.total_revenue)}
                        </td>
                        <td>
                          <div style={{ 
                            background: '#ecf0f1',
                            borderRadius: '4px',
                            height: '8px',
                            overflow: 'hidden'
                          }}>
                            <div 
                              style={{
                                background: '#3498db',
                                height: '100%',
                                width: `${(item.total_quantity / stats.popularItems[0].total_quantity) * 100}%`,
                                transition: 'width 0.3s ease'
                              }}
                            />
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
