import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [backendStatus, setBackendStatus] = useState('checking');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [isRealTimeConnected, setIsRealTimeConnected] = useState(false);
  const [connectionError, setConnectionError] = useState(null);

  useEffect(() => {
    checkBackend();
    fetchDashboardData();
    setupRealTimeConnection();
    
    return () => {
      if (window.dashboardEventSource) {
        console.log('ðŸ§¹ Cleaning up SSE connection');
        window.dashboardEventSource.close();
      }
    };
  }, []);

  // ðŸ”¥ REAL-TIME CONNECTION WITH BETTER ERROR HANDLING
  const setupRealTimeConnection = () => {
    try {
      console.log('ðŸ”„ Setting up real-time connection...');
      
      const eventSource = new EventSource('http://localhost:5001/api/dashboard/stream');
      window.dashboardEventSource = eventSource;
      
      eventSource.onopen = () => {
        console.log('âœ… Dashboard real-time connection established');
        setIsRealTimeConnected(true);
        setConnectionError(null);
      };
      
      eventSource.onmessage = (event) => {
        try {
          const update = JSON.parse(event.data);
          console.log('ðŸ“¨ Dashboard real-time update received:', update);
          
          if (update.type === 'dashboard_update') {
            setDashboardData(update.data);
            setLastUpdate(new Date());
            setLoading(false);
          } else if (update.type === 'connected') {
            console.log('ðŸ”— SSE connection confirmed');
          }
        } catch (parseError) {
          console.error('âŒ Error parsing SSE message:', parseError);
        }
      };
      
      eventSource.onerror = (error) => {
        console.error('âŒ Dashboard real-time connection error:', error);
        setIsRealTimeConnected(false);
        setConnectionError('Failed to connect to real-time updates');
        
        // Close and attempt reconnect after 3 seconds
        eventSource.close();
        setTimeout(() => {
          console.log('ðŸ”„ Attempting to reconnect...');
          setupRealTimeConnection();
        }, 3000);
      };
      
    } catch (error) {
      console.error('âŒ Error setting up dashboard real-time connection:', error);
      setConnectionError('Real-time updates not supported');
      setIsRealTimeConnected(false);
    }
  };

  const checkBackend = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/health');
      if (response.ok) {
        setBackendStatus('connected');
      } else {
        setBackendStatus('error');
      }
    } catch (error) {
      setBackendStatus('disconnected');
    }
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5001/api/dashboard/stats');
      const result = await response.json();
      
      if (result.success) {
        setDashboardData(result.data);
        setLastUpdate(new Date());
      } else {
        console.error('Failed to fetch dashboard data');
      }
    } catch (error) {
      console.error('Error fetching dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const testSSEConnection = () => {
    console.log('ðŸ§ª Testing SSE connection...');
    setupRealTimeConnection();
  };

  // ... rest of your functions (calculateActiveOrders, formatCurrency, etc.)

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>Dashboard</h1>
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          {lastUpdate && (
            <div style={{ fontSize: '0.8rem', color: '#666' }}>
              Last update: {lastUpdate.toLocaleTimeString()}
            </div>
          )}
          <div style={{ 
            padding: '0.25rem 0.5rem',
            background: isRealTimeConnected ? '#d4edda' : '#f8d7da',
            color: isRealTimeConnected ? '#155724' : '#721c24',
            borderRadius: '4px',
            fontSize: '0.7rem',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
            gap: '0.25rem',
            cursor: 'pointer'
          }}
          onClick={testSSEConnection}
          title="Click to test connection"
          >
            <div style={{ 
              width: '8px', 
              height: '8px', 
              borderRadius: '50%',
              background: isRealTimeConnected ? '#28a745' : '#dc3545',
              animation: isRealTimeConnected ? 'pulse 1.5s infinite' : 'none'
            }}></div>
            {isRealTimeConnected ? 'LIVE' : 'OFFLINE'}
          </div>
        </div>
      </div>

      {/* Connection Error Alert */}
      {connectionError && (
        <div className="card" style={{ borderLeft: '4px solid #e74c3c', background: '#f8d7da' }}>
          <div style={{ color: '#721c24', padding: '1rem' }}>
            <strong>Connection Issue:</strong> {connectionError}
            <button 
              onClick={() => setConnectionError(null)}
              style={{ 
                marginLeft: '1rem',
                background: 'none',
                border: 'none',
                color: '#721c24',
                cursor: 'pointer',
                fontSize: '1.2rem'
              }}
            >
              Ã—
            </button>
          </div>
        </div>
      )}

      {/* ... rest of your JSX ... */}
      
      <style jsx>{`
        @keyframes pulse {
          0% { opacity: 1; }
          50% { opacity: 0.5; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
