import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [backendStatus, setBackendStatus] = useState('checking');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [isRealTimeConnected, setIsRealTimeConnected] = useState(false);

  useEffect(() => {
    checkBackend();
    fetchDashboardData();
    setupRealTimeConnection(); // üî• Setup real-time connection
    
    return () => {
      // Cleanup real-time connection ketika component unmount
      if (window.dashboardEventSource) {
        window.dashboardEventSource.close();
      }
    };
  }, []);

  // üî• REAL-TIME CONNECTION FOR DASHBOARD
  const setupRealTimeConnection = () => {
    try {
      const eventSource = new EventSource('http://localhost:5001/api/dashboard/stream');
      window.dashboardEventSource = eventSource;
      
      eventSource.onopen = () => {
        console.log('üîó Dashboard real-time connection established');
        setIsRealTimeConnected(true);
      };
      
      eventSource.onmessage = (event) => {
        const update = JSON.parse(event.data);
        console.log('üì® Dashboard real-time update received:', update);
        
        if (update.type === 'dashboard_update') {
          setDashboardData(update.data);
          setLastUpdate(new Date());
          setLoading(false);
        }
      };
      
      eventSource.onerror = (error) => {
        console.error('‚ùå Dashboard real-time connection error:', error);
        setIsRealTimeConnected(false);
        // Attempt reconnect after 5 seconds
        setTimeout(setupRealTimeConnection, 5000);
      };
      
    } catch (error) {
      console.error('Error setting up dashboard real-time connection:', error);
    }
  };

  const checkBackend = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/health');
      if (response.ok) {
        setBackendStatus('connected');
      } else {
        setBackendStatus('error');
      }
    } catch (error) {
      setBackendStatus('disconnected');
    }
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5001/api/dashboard/stats');
      const result = await response.json();
      
      if (result.success) {
        setDashboardData(result.data);
        setLastUpdate(new Date());
      } else {
        console.error('Failed to fetch dashboard data');
      }
    } catch (error) {
      console.error('Error fetching dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const testEndpoints = async () => {
    const endpoints = [
      { name: 'Health', url: 'http://localhost:5001/api/health' },
      { name: 'Menu', url: 'http://localhost:5001/api/menu' },
      { name: 'Orders', url: 'http://localhost:5001/api/orders' },
      { name: 'Dashboard', url: 'http://localhost:5001/api/dashboard/stats' }
    ];

    for (const endpoint of endpoints) {
      try {
        const response = await fetch(endpoint.url);
        const data = await response.json();
        console.log(`${endpoint.name}:`, data);
      } catch (error) {
        console.error(`${endpoint.name} error:`, error);
      }
    }
    
    fetchDashboardData();
  };

  // Hitung active orders dari ordersByStatus
  const calculateActiveOrders = () => {
    if (!dashboardData?.ordersByStatus) return 0;
    
    return dashboardData.ordersByStatus
      .filter(status => ['pending', 'confirmed', 'preparing', 'ready'].includes(status.status))
      .reduce((sum, status) => sum + status.count, 0);
  };

  // Format currency untuk revenue
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  // Format waktu last update
  const formatLastUpdate = (date) => {
    if (!date) return '';
    return date.toLocaleTimeString('id-ID', { 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    });
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>Dashboard</h1>
        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          {lastUpdate && (
            <div style={{ fontSize: '0.8rem', color: '#666' }}>
              Last update: {formatLastUpdate(lastUpdate)}
            </div>
          )}
          <div style={{ 
            padding: '0.25rem 0.5rem',
            background: isRealTimeConnected ? '#d4edda' : '#f8d7da',
            color: isRealTimeConnected ? '#155724' : '#721c24',
            borderRadius: '4px',
            fontSize: '0.7rem',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
            gap: '0.25rem'
          }}>
            <div style={{ 
              width: '8px', 
              height: '8px', 
              borderRadius: '50%',
              background: isRealTimeConnected ? '#28a745' : '#dc3545',
              animation: isRealTimeConnected ? 'pulse 1.5s infinite' : 'none'
            }}></div>
            {isRealTimeConnected ? 'LIVE' : 'OFFLINE'}
          </div>
        </div>
      </div>
      
      <div className="card">
        <h2>System Status</h2>
        <div style={{ 
          padding: '1rem',
          background: backendStatus === 'connected' ? '#d4edda' : '#f8d7da',
          border: `2px solid ${backendStatus === 'connected' ? '#c3e6cb' : '#f5c6cb'}`,
          borderRadius: '8px',
          marginBottom: '1rem'
        }}>
          <strong>Backend Status:</strong> 
          <span style={{ 
            color: backendStatus === 'connected' ? '#155724' : '#721c24',
            marginLeft: '0.5rem',
            fontWeight: 'bold'
          }}>
            {backendStatus.toUpperCase()}
          </span>
        </div>

        <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap', alignItems: 'center' }}>
          <button 
            className="btn btn-primary"
            onClick={testEndpoints}
          >
            üîç Test All Endpoints
          </button>
          
          <button 
            className="btn btn-secondary"
            onClick={() => {
              checkBackend();
              fetchDashboardData();
            }}
          >
            üîÑ Refresh Now
          </button>

          <div style={{ 
            padding: '0.5rem 1rem', 
            background: isRealTimeConnected ? '#e7f3ff' : '#f8f9fa',
            border: `1px solid ${isRealTimeConnected ? '#b3d9ff' : '#dee2e6'}`,
            borderRadius: '4px',
            fontSize: '0.8rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            {isRealTimeConnected ? 'üü¢ Real-time Active' : 'üî¥ Real-time Offline'}
            {isRealTimeConnected && (
              <div style={{ 
                width: '6px', 
                height: '6px', 
                borderRadius: '50%',
                background: '#28a745',
                animation: 'pulse 1.5s infinite'
              }}></div>
            )}
          </div>
        </div>
      </div>

      {/* Quick Stats */}
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Orders</h3>
          <div className="number">
            {loading ? '...' : (dashboardData?.totalOrders || 0)}
          </div>
          <div className="trend">
            {loading ? 'Loading...' : `Today: ${dashboardData?.todayOrders || 0} orders`}
          </div>
        </div>
        
        <div className="stat-card">
          <h3>Total Revenue</h3>
          <div className="number">
            {loading ? '...' : formatCurrency(dashboardData?.totalRevenue || 0)}
          </div>
          <div className="trend">
            {loading ? 'Loading...' : `Today: ${formatCurrency(dashboardData?.todayRevenue || 0)}`}
          </div>
        </div>
        
        <div className="stat-card">
          <h3>Active Orders</h3>
          <div className="number">
            {loading ? '...' : calculateActiveOrders()}
          </div>
          <div className="trend">
            {loading ? 'Loading...' : 'Pending + Confirmed + Preparing + Ready'}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card">
        <h2>Quick Actions</h2>
        <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
          <a 
            href="/order" 
            target="_blank" 
            rel="noopener noreferrer"
            className="btn btn-success"
          >
            üõí Customer Order Page
          </a>
          <a 
            href="/menu" 
            className="btn btn-primary"
          >
            üçΩÔ∏è Menu Management
          </a>
          <a 
            href="/orders" 
            className="btn btn-warning"
          >
            üìã Order Management
          </a>
        </div>
      </div>

      {/* Status Breakdown */}
      {dashboardData?.ordersByStatus && (
        <div className="card">
          <h2>Orders Breakdown</h2>
          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
            {dashboardData.ordersByStatus.map((status, index) => (
              <div 
                key={index}
                style={{
                  padding: '0.75rem 1rem',
                  background: '#f8f9fa',
                  border: '1px solid #dee2e6',
                  borderRadius: '8px',
                  minWidth: '120px',
                  textAlign: 'center',
                  transition: 'all 0.3s ease'
                }}
              >
                <div style={{ 
                  fontSize: '0.8rem', 
                  color: '#666',
                  textTransform: 'capitalize'
                }}>
                  {status.status}
                </div>
                <div style={{ 
                  fontSize: '1.5rem', 
                  fontWeight: 'bold',
                  color: ['pending', 'confirmed', 'preparing', 'ready'].includes(status.status) ? '#dc3545' : '#28a745'
                }}>
                  {status.count}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Real-time Status Info */}
      <div className="card" style={{ background: '#f8f9fa' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <h3 style={{ margin: 0, color: '#2c3e50' }}>Real-time Status</h3>
            <p style={{ margin: '0.5rem 0 0 0', color: '#7f8c8d', fontSize: '0.9rem' }}>
              {isRealTimeConnected 
                ? 'Dashboard is receiving live updates automatically' 
                : 'Connecting to real-time updates...'
              }
            </p>
          </div>
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: '0.5rem',
            padding: '0.5rem 1rem',
            background: isRealTimeConnected ? '#d4edda' : '#fff3cd',
            color: isRealTimeConnected ? '#155724' : '#856404',
            borderRadius: '4px',
            fontSize: '0.8rem',
            fontWeight: 'bold'
          }}>
            {isRealTimeConnected ? '‚úÖ Connected' : 'üîÑ Connecting...'}
          </div>
        </div>
      </div>

      {/* CSS Animation for Live Indicator */}
      <style jsx>{`
        @keyframes pulse {
          0% { opacity: 1; }
          50% { opacity: 0.5; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
