import React, { useState, useEffect, useRef } from 'react';
import { menuAPI, ordersAPI } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';

const CustomerOrder = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [activeCategory, setActiveCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [orderLoading, setOrderLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [itemAdded, setItemAdded] = useState(null);
  const [cartPulse, setCartPulse] = useState(false);
  
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    table_number: ''
  });

  const cartRef = useRef(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    fetchMenuData();
    
    // Focus search input on mount
    if (searchInputRef.current) {
      setTimeout(() => {
        searchInputRef.current.focus();
      }, 500);
    }
  }, []);

  const fetchMenuData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [menuResponse, categoriesResponse] = await Promise.all([
        menuAPI.getAll(),
        menuAPI.getCategories()
      ]);

      setMenuItems(menuResponse.data.data);
      setCategories(categoriesResponse.data.data);
    } catch (err) {
      console.error('Error fetching menu data:', err);
      setError('Failed to load menu. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    
    if (existingItem) {
      setCart(cart.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { 
        ...item, 
        quantity: 1,
        notes: ''
      }]);
    }

    // Show added feedback
    setItemAdded(item.id);
    setCartPulse(true);
    
    // Trigger cart animation
    if (cartRef.current) {
      cartRef.current.style.transform = 'scale(1.1)';
      setTimeout(() => {
        if (cartRef.current) {
          cartRef.current.style.transform = 'scale(1)';
        }
      }, 300);
    }
    
    setTimeout(() => {
      setItemAdded(null);
      setCartPulse(false);
    }, 1000);
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item.id !== itemId));
    
    // Show removal feedback
    const menuItem = document.querySelector(`[data-item-id="${itemId}"]`);
    if (menuItem) {
      menuItem.style.animation = 'shake 0.5s ease-in-out';
      setTimeout(() => {
        if (menuItem) menuItem.style.animation = '';
      }, 500);
    }
  };

  const updateQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(itemId);
      return;
    }
    
    setCart(cart.map(item =>
      item.id === itemId
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const updateItemNotes = (itemId, notes) => {
    setCart(cart.map(item =>
      item.id === itemId
        ? { ...item, notes }
        : item
    ));
  };

  const getTotalAmount = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getItemCountInCart = (itemId) => {
    const item = cart.find(cartItem => cartItem.id === itemId);
    return item ? item.quantity : 0;
  };

  const getCartTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const handleSubmitOrder = async (e) => {
    e.preventDefault();
    
    if (cart.length === 0) {
      setError('Please add items to your cart before ordering');
      return;
    }

    if (!customerInfo.name.trim()) {
      setError('Please enter your name');
      return;
    }

    if (!customerInfo.phone.trim()) {
      setError('Please enter your phone number');
      return;
    }

    try {
      setOrderLoading(true);
      setError(null);

      const orderData = {
        customer_name: customerInfo.name.trim(),
        customer_phone: customerInfo.phone.trim(),
        table_number: customerInfo.table_number.trim() || undefined,
        items: cart.map(item => ({
          menu_item_id: item.id,
          quantity: item.quantity,
          notes: item.notes || ''
        })),
        notes: ''
      };

      const response = await ordersAPI.create(orderData);
      
      setOrderSuccess(true);
      setCart([]);
      setCustomerInfo({ name: '', phone: '', table_number: '' });
      setShowCartPopup(false);
      
      setTimeout(() => {
        setOrderSuccess(false);
      }, 5000);

    } catch (err) {
      console.error('Error placing order:', err);
      setError(err.response?.data?.message || 'Failed to place order. Please try again.');
    } finally {
      setOrderLoading(false);
    }
  };

  const clearCart = () => {
    setCart([]);
    // Show clear animation
    const popupBody = document.querySelector('[data-popup-body]');
    if (popupBody) {
      popupBody.style.animation = 'fadeOut 0.3s ease';
      setTimeout(() => {
        if (popupBody) popupBody.style.animation = 'fadeIn 0.3s ease';
      }, 300);
    }
  };

  const quickAddItem = (item, quantity = 1) => {
    for (let i = 0; i < quantity; i++) {
      setTimeout(() => addToCart(item), i * 100);
    }
  };

  // Filter items based on category and search term
  const filteredItems = menuItems.filter(item => {
    const matchesCategory = activeCategory === 'all' || item.category_id === parseInt(activeCategory);
    const matchesSearch = item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         item.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  if (loading) {
    return (
      <div style={styles.customerOrder}>
        <div style={styles.orderContainer}>
          <LoadingSpinner text="Loading menu..." />
        </div>
      </div>
    );
  }

  return (
    <div style={styles.customerOrder}>
      {/* Floating Cart Button */}
      {cart.length > 0 && (
        <div 
          ref={cartRef}
          style={{
            ...styles.floatingCartButton,
            ...(cartPulse ? styles.cartPulse : {})
          }} 
          onClick={() => setShowCartPopup(true)}
          title="View Cart"
        >
          <div style={styles.cartIcon}>ðŸ›’</div>
          <div style={styles.cartBadge}>{getCartTotalItems()}</div>
          <div style={styles.cartTotalPreview}>
            {formatCurrency(getTotalAmount())}
          </div>
        </div>
      )}

      <div style={styles.orderContainer}>
        <div style={styles.orderHeader}>
          <h1 style={styles.title}>
            <span style={styles.titleIcon}>ðŸ½ï¸</span>
            Welcome to Kongkow Coffee
          </h1>
          <p style={styles.subtitle}>Browse our menu and place your order below</p>
          
          {/* Quick Stats */}
          <div style={styles.quickStats}>
            <div style={styles.statItem}>
              <span style={styles.statNumber}>{menuItems.length}</span>
              <span style={styles.statLabel}>Menu Items</span>
            </div>
            <div style={styles.statItem}>
              <span style={styles.statNumber}>{categories.length}</span>
              <span style={styles.statLabel}>Categories</span>
            </div>
            <div style={styles.statItem}>
              <span style={styles.statNumber}>{getCartTotalItems()}</span>
              <span style={styles.statLabel}>In Your Cart</span>
            </div>
          </div>
        </div>
        
        {/* Menu Section */}
        <div style={styles.menuSection}>
          {error && !orderSuccess && (
            <div style={styles.errorAlert}>
              <div style={styles.alertIcon}>âŒ</div>
              <div style={styles.alertContent}>
                {error}
                <button 
                  onClick={() => setError(null)}
                  style={styles.closeButton}
                >
                  Ã—
                </button>
              </div>
            </div>
          )}

          {orderSuccess && (
            <div style={styles.successAlert}>
              <div style={styles.alertIcon}>âœ…</div>
              <div style={styles.alertContent}>
                <strong>Order Placed Successfully!</strong> Your food is being prepared.
              </div>
            </div>
          )}

          <h2 style={styles.sectionTitle}>
            <span style={styles.sectionIcon}>ðŸ“‹</span>
            Our Delicious Menu
          </h2>
          
          {/* Search Bar */}
          <div style={styles.searchBar}>
            <input
              ref={searchInputRef}
              type="text"
              placeholder="ðŸ” Search menu items..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={styles.searchInput}
            />
            {searchTerm && (
              <button 
                onClick={() => setSearchTerm('')}
                style={styles.clearSearchButton}
                title="Clear search"
              >
                âœ•
              </button>
            )}
          </div>
          
          {/* Category Tabs */}
          <div style={styles.categoryTabs}>
            <button
              style={{
                ...styles.categoryTab,
                ...(activeCategory === 'all' ? styles.activeCategoryTab : {})
              }}
              onClick={() => setActiveCategory('all')}
            >
              <span style={styles.categoryIcon}>ðŸ•</span>
              All Items
            </button>
            {categories.map(category => (
              <button
                key={category.id}
                style={{
                  ...styles.categoryTab,
                  ...(activeCategory === category.id.toString() ? styles.activeCategoryTab : {})
                }}
                onClick={() => setActiveCategory(category.id.toString())}
              >
                {category.icon || 'ðŸ½ï¸'} {category.name}
              </button>
            ))}
          </div>

          {/* Search Results Info */}
          {searchTerm && (
            <div style={styles.searchResults}>
              <strong>Search Results:</strong> Showing {filteredItems.length} item(s) for "{searchTerm}"
              <button 
                onClick={() => setSearchTerm('')}
                style={styles.clearSearchButton}
              >
                Clear Search
              </button>
            </div>
          )}
          
          {/* Menu Items Grid */}
          <div style={styles.menuGrid}>
            {filteredItems.map(item => (
              <div 
                key={item.id} 
                style={{
                  ...styles.menuItem,
                  ...(itemAdded === item.id ? styles.itemAdded : {}),
                  ...(getItemCountInCart(item.id) > 0 ? styles.itemInCart : {})
                }}
                data-item-id={item.id}
              >
                <div style={styles.itemHeader}>
                  <h4 style={styles.itemName}>{item.name}</h4>
                  {item.is_popular && (
                    <span style={styles.popularBadge}>ðŸ”¥ Popular</span>
                  )}
                  {item.is_new && (
                    <span style={styles.newBadge}>ðŸ†• New</span>
                  )}
                </div>
                
                <div style={styles.price}>{formatCurrency(item.price)}</div>
                <p style={styles.description}>{item.description}</p>
                
                {item.preparation_time && (
                  <div style={styles.prepTime}>
                    â±ï¸ Prep time: {item.preparation_time} minutes
                  </div>
                )}
                
                <div style={styles.quantityControls}>
                  <button 
                    style={styles.quantityBtn}
                    onClick={() => updateQuantity(item.id, getItemCountInCart(item.id) - 1)}
                    disabled={getItemCountInCart(item.id) === 0}
                    title="Decrease quantity"
                  >
                    -
                  </button>
                  
                  <span style={styles.quantityDisplay}>
                    {getItemCountInCart(item.id) > 0 ? (
                      <>
                        <span style={styles.inCartIcon}>âœ…</span>
                        In cart: {getItemCountInCart(item.id)}
                      </>
                    ) : (
                      'Not in cart'
                    )}
                  </span>
                  
                  <button 
                    style={styles.quantityBtn}
                    onClick={() => addToCart(item)}
                    title="Add to cart"
                  >
                    +
                  </button>
                </div>

                {/* Quick Add Buttons */}
                {getItemCountInCart(item.id) === 0 && (
                  <div style={styles.quickAddButtons}>
                    <button 
                      style={styles.quickAddBtn}
                      onClick={() => quickAddItem(item, 2)}
                    >
                      Add 2
                    </button>
                    <button 
                      style={styles.quickAddBtn}
                      onClick={() => quickAddItem(item, 3)}
                    >
                      Add 3
                    </button>
                  </div>
                )}

                {getItemCountInCart(item.id) > 0 && (
                  <div style={styles.notesContainer}>
                    <input
                      type="text"
                      placeholder="ðŸ’¡ Special instructions (optional)"
                      value={cart.find(cartItem => cartItem.id === item.id)?.notes || ''}
                      onChange={(e) => updateItemNotes(item.id, e.target.value)}
                      style={styles.notesInput}
                    />
                  </div>
                )}
              </div>
            ))}
          </div>

          {filteredItems.length === 0 && (
            <div style={styles.emptyMenu}>
              <div style={styles.emptyIcon}>ðŸ”</div>
              <h3 style={styles.emptyTitle}>No items found</h3>
              <p style={styles.emptyText}>
                {searchTerm 
                  ? `No items found for "${searchTerm}". Try a different search term or category.`
                  : 'No items in this category. Please select a different category or check back later.'
                }
              </p>
              {searchTerm && (
                <button 
                  onClick={() => setSearchTerm('')}
                  style={styles.clearSearchButtonLarge}
                >
                  Clear Search
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Cart Popup */}
      {showCartPopup && (
        <div style={styles.popupOverlay} onClick={() => setShowCartPopup(false)}>
          <div style={styles.popupContent} onClick={(e) => e.stopPropagation()}>
            <div style={styles.popupHeader}>
              <h2 style={styles.popupTitle}>
                <span style={styles.cartIcon}>ðŸ›’</span>
                Your Order
                {cart.length > 0 && (
                  <span style={styles.cartItemCount}>({getCartTotalItems()} items)</span>
                )}
              </h2>
              <div style={styles.popupHeaderActions}>
                {cart.length > 0 && (
                  <button 
                    style={styles.clearCartButton}
                    onClick={clearCart}
                    title="Clear entire cart"
                  >
                    ðŸ—‘ï¸ Clear All
                  </button>
                )}
                <button 
                  style={styles.closePopupButton}
                  onClick={() => setShowCartPopup(false)}
                  title="Close cart"
                >
                  Ã—
                </button>
              </div>
            </div>
            
            <div style={styles.popupBody} data-popup-body>
              {cart.length === 0 ? (
                <div style={styles.emptyCart}>
                  <div style={styles.emptyCartIcon}>ðŸ›’</div>
                  <h3 style={styles.emptyCartTitle}>Your cart is empty</h3>
                  <p style={styles.emptyCartText}>Add some delicious items from the menu!</p>
                  <button 
                    style={styles.continueShoppingButton}
                    onClick={() => setShowCartPopup(false)}
                  >
                    Continue Shopping
                  </button>
                </div>
              ) : (
                <>
                  <div style={styles.cartItems}>
                    {cart.map(item => (
                      <div key={item.id} style={styles.cartItem}>
                        <div style={styles.cartItemInfo}>
                          <div style={styles.cartItemName}>{item.name}</div>
                          <div style={styles.cartItemPrice}>
                            {formatCurrency(item.price)} Ã— {item.quantity}
                          </div>
                          {item.notes && (
                            <div style={styles.cartItemNotes}>
                              ðŸ“ {item.notes}
                            </div>
                          )}
                        </div>
                        <div style={styles.cartQuantityControls}>
                          <button 
                            style={styles.quantityBtnSmall}
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            title="Decrease quantity"
                          >
                            -
                          </button>
                          <span style={styles.quantityDisplaySmall}>{item.quantity}</span>
                          <button 
                            style={styles.quantityBtnSmall}
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            title="Increase quantity"
                          >
                            +
                          </button>
                          <button 
                            style={styles.removeButton}
                            onClick={() => removeFromCart(item.id)}
                            title="Remove item"
                          >
                            ðŸ—‘ï¸
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                  
                  <div style={styles.cartSummary}>
                    <div style={styles.cartTotal}>
                      <span>Total:</span>
                      <span style={styles.totalAmount}>{formatCurrency(getTotalAmount())}</span>
                    </div>
                    <div style={styles.itemCount}>
                      {getCartTotalItems()} items â€¢ {cart.length} unique items
                    </div>
                  </div>
                  
                  <form style={styles.orderForm} onSubmit={handleSubmitOrder}>
                    <h3 style={styles.formTitle}>
                      <span style={styles.formIcon}>ðŸ‘¤</span>
                      Customer Information
                    </h3>
                    
                    <div style={styles.formGroup}>
                      <label style={styles.formLabel}>Your Name *</label>
                      <input
                        type="text"
                        style={styles.formControl}
                        value={customerInfo.name}
                        onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                        placeholder="Enter your name"
                        required
                      />
                    </div>
                    
                    <div style={styles.formGroup}>
                      <label style={styles.formLabel}>Phone Number *</label>
                      <input
                        type="tel"
                        style={styles.formControl}
                        value={customerInfo.phone}
                        onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                        placeholder="Enter your phone number"
                        required
                      />
                    </div>
                    
                    <div style={styles.formGroup}>
                      <label style={styles.formLabel}>Table Number (if dining in)</label>
                      <input
                        type="text"
                        style={styles.formControl}
                        value={customerInfo.table_number}
                        onChange={(e) => setCustomerInfo({...customerInfo, table_number: e.target.value})}
                        placeholder="e.g., Table 1, Table 5"
                      />
                    </div>
                    
                    <button 
                      type="submit" 
                      style={{
                        ...styles.submitButton,
                        ...(orderLoading || cart.length === 0 ? styles.submitButtonDisabled : {})
                      }}
                      disabled={orderLoading || cart.length === 0}
                    >
                      {orderLoading ? (
                        <>
                          <div style={styles.spinner}></div>
                          Placing Order...
                        </>
                      ) : (
                        <>
                          <span style={styles.submitIcon}>ðŸŽ¯</span>
                          Place Order - {formatCurrency(getTotalAmount())}
                        </>
                      )}
                    </button>

                    <div style={styles.termsText}>
                      By placing order, you agree to our terms and conditions
                    </div>
                  </form>
                </>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

