import React, { useState, useEffect, useRef } from 'react';
import { menuAPI, ordersAPI } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';
import './CustomerOrder.css'; // Kita akan buat file CSS terpisah

const CustomerOrder = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [activeCategory, setActiveCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [orderLoading, setOrderLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [itemAdded, setItemAdded] = useState(null);
  const [cartPulse, setCartPulse] = useState(false);
  
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    table_number: ''
  });

  const cartRef = useRef(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    fetchMenuData();
  }, []);

  const fetchMenuData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Simulasi data untuk testing
      const mockMenuItems = [
        {
          id: 1,
          name: 'Espresso',
          description: 'Strong black coffee',
          price: 25000,
          category_id: 1,
          preparation_time: 5,
          is_popular: true
        },
        {
          id: 2,
          name: 'Cappuccino',
          description: 'Coffee with steamed milk',
          price: 35000,
          category_id: 1,
          preparation_time: 7,
          is_popular: true
        },
        {
          id: 3,
          name: 'Chocolate Cake',
          description: 'Rich chocolate cake slice',
          price: 45000,
          category_id: 2,
          preparation_time: 2,
          is_new: true
        }
      ];

      const mockCategories = [
        { id: 1, name: 'Coffee', icon: 'â˜•' },
        { id: 2, name: 'Desserts', icon: 'ðŸ°' },
        { id: 3, name: 'Sandwiches', icon: 'ðŸ¥ª' }
      ];

      // Gunakan data mock untuk testing
      setMenuItems(mockMenuItems);
      setCategories(mockCategories);

      // Jika API ready, gunakan ini:
      // const [menuResponse, categoriesResponse] = await Promise.all([
      //   menuAPI.getAll(),
      //   menuAPI.getCategories()
      // ]);
      // setMenuItems(menuResponse.data.data);
      // setCategories(categoriesResponse.data.data);

    } catch (err) {
      console.error('Error fetching menu data:', err);
      setError('Failed to load menu. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    
    if (existingItem) {
      setCart(cart.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { 
        ...item, 
        quantity: 1,
        notes: ''
      }]);
    }

    // Show added feedback
    setItemAdded(item.id);
    setCartPulse(true);
    
    setTimeout(() => {
      setItemAdded(null);
      setCartPulse(false);
    }, 1000);
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item.id !== itemId));
  };

  const updateQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(itemId);
      return;
    }
    
    setCart(cart.map(item =>
      item.id === itemId
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const updateItemNotes = (itemId, notes) => {
    setCart(cart.map(item =>
      item.id === itemId
        ? { ...item, notes }
        : item
    ));
  };

  const getTotalAmount = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getItemCountInCart = (itemId) => {
    const item = cart.find(cartItem => cartItem.id === itemId);
    return item ? item.quantity : 0;
  };

  const getCartTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const handleSubmitOrder = async (e) => {
    e.preventDefault();
    
    if (cart.length === 0) {
      setError('Please add items to your cart before ordering');
      return;
    }

    if (!customerInfo.name.trim()) {
      setError('Please enter your name');
      return;
    }

    if (!customerInfo.phone.trim()) {
      setError('Please enter your phone number');
      return;
    }

    try {
      setOrderLoading(true);
      setError(null);

      const orderData = {
        customer_name: customerInfo.name.trim(),
        customer_phone: customerInfo.phone.trim(),
        table_number: customerInfo.table_number.trim() || undefined,
        items: cart.map(item => ({
          menu_item_id: item.id,
          quantity: item.quantity,
          notes: item.notes || ''
        })),
        notes: ''
      };

      // Simulasi success order
      console.log('Order data:', orderData);
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      setOrderSuccess(true);
      setCart([]);
      setCustomerInfo({ name: '', phone: '', table_number: '' });
      setShowCartPopup(false);
      
      setTimeout(() => {
        setOrderSuccess(false);
      }, 5000);

    } catch (err) {
      console.error('Error placing order:', err);
      setError(err.response?.data?.message || 'Failed to place order. Please try again.');
    } finally {
      setOrderLoading(false);
    }
  };

  const clearCart = () => {
    setCart([]);
  };

  const quickAddItem = (item, quantity = 1) => {
    for (let i = 0; i < quantity; i++) {
      addToCart(item);
    }
  };

  // Filter items based on category and search term
  const filteredItems = menuItems.filter(item => {
    const matchesCategory = activeCategory === 'all' || item.category_id === parseInt(activeCategory);
    const matchesSearch = item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         item.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  if (loading) {
    return (
      <div className="customer-order">
        <div className="order-container">
          <LoadingSpinner text="Loading menu..." />
        </div>
      </div>
    );
  }

  return (
    <div className="customer-order">
      {/* Floating Cart Button */}
      {cart.length > 0 && (
        <div 
          ref={cartRef}
          className={`floating-cart-button ${cartPulse ? 'cart-pulse' : ''}`}
          onClick={() => setShowCartPopup(true)}
          title="View Cart"
        >
          <div className="cart-icon">ðŸ›’</div>
          <div className="cart-badge">{getCartTotalItems()}</div>
          <div className="cart-total-preview">
            {formatCurrency(getTotalAmount())}
          </div>
        </div>
      )}

      <div className="order-container">
        <div className="order-header">
          <h1 className="title">
            <span className="title-icon">ðŸ½ï¸</span>
            Welcome to Kongkow Coffee
          </h1>
          <p className="subtitle">Browse our menu and place your order below</p>
          
          {/* Quick Stats */}
          <div className="quick-stats">
            <div className="stat-item">
              <span className="stat-number">{menuItems.length}</span>
              <span className="stat-label">Menu Items</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{categories.length}</span>
              <span className="stat-label">Categories</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{getCartTotalItems()}</span>
              <span className="stat-label">In Your Cart</span>
            </div>
          </div>
        </div>
        
        {/* Menu Section */}
        <div className="menu-section">
          {error && !orderSuccess && (
            <div className="error-alert">
              <div className="alert-icon">âŒ</div>
              <div className="alert-content">
                {error}
                <button 
                  onClick={() => setError(null)}
                  className="close-button"
                >
                  Ã—
                </button>
              </div>
            </div>
          )}

          {orderSuccess && (
            <div className="success-alert">
              <div className="alert-icon">âœ…</div>
              <div className="alert-content">
                <strong>Order Placed Successfully!</strong> Your food is being prepared.
              </div>
            </div>
          )}

          <h2 className="section-title">
            <span className="section-icon">ðŸ“‹</span>
            Our Delicious Menu
          </h2>
          
          {/* Search Bar */}
          <div className="search-bar">
            <input
              ref={searchInputRef}
              type="text"
              placeholder="ðŸ” Search menu items..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button 
                onClick={() => setSearchTerm('')}
                className="clear-search-button"
                title="Clear search"
              >
                âœ•
              </button>
            )}
          </div>
          
          {/* Category Tabs */}
          <div className="category-tabs">
            <button
              className={`category-tab ${activeCategory === 'all' ? 'active' : ''}`}
              onClick={() => setActiveCategory('all')}
            >
              <span className="category-icon">ðŸ•</span>
              All Items
            </button>
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-tab ${activeCategory === category.id.toString() ? 'active' : ''}`}
                onClick={() => setActiveCategory(category.id.toString())}
              >
                {category.icon} {category.name}
              </button>
            ))}
          </div>

          {/* Search Results Info */}
          {searchTerm && (
            <div className="search-results">
              <strong>Search Results:</strong> Showing {filteredItems.length} item(s) for "{searchTerm}"
              <button 
                onClick={() => setSearchTerm('')}
                className="clear-search-button"
              >
                Clear Search
              </button>
            </div>
          )}
          
          {/* Menu Items Grid */}
          <div className="menu-grid">
            {filteredItems.map(item => (
              <div 
                key={item.id} 
                className={`menu-item ${itemAdded === item.id ? 'item-added' : ''} ${getItemCountInCart(item.id) > 0 ? 'item-in-cart' : ''}`}
                data-item-id={item.id}
              >
                <div className="item-header">
                  <h4 className="item-name">{item.name}</h4>
                  <div className="item-badges">
                    {item.is_popular && (
                      <span className="popular-badge">ðŸ”¥ Popular</span>
                    )}
                    {item.is_new && (
                      <span className="new-badge">ðŸ†• New</span>
                    )}
                  </div>
                </div>
                
                <div className="price">{formatCurrency(item.price)}</div>
                <p className="description">{item.description}</p>
                
                {item.preparation_time && (
                  <div className="prep-time">
                    â±ï¸ Prep time: {item.preparation_time} minutes
                  </div>
                )}
                
                <div className="quantity-controls">
                  <button 
                    className="quantity-btn"
                    onClick={() => updateQuantity(item.id, getItemCountInCart(item.id) - 1)}
                    disabled={getItemCountInCart(item.id) === 0}
                    title="Decrease quantity"
                  >
                    -
                  </button>
                  
                  <span className="quantity-display">
                    {getItemCountInCart(item.id) > 0 ? (
                      <>
                        <span className="in-cart-icon">âœ…</span>
                        In cart: {getItemCountInCart(item.id)}
                      </>
                    ) : (
                      'Not in cart'
                    )}
                  </span>
                  
                  <button 
                    className="quantity-btn"
                    onClick={() => addToCart(item)}
                    title="Add to cart"
                  >
                    +
                  </button>
                </div>

                {/* Quick Add Buttons */}
                {getItemCountInCart(item.id) === 0 && (
                  <div className="quick-add-buttons">
                    <button 
                      className="quick-add-btn"
                      onClick={() => quickAddItem(item, 2)}
                    >
                      Add 2
                    </button>
                    <button 
                      className="quick-add-btn"
                      onClick={() => quickAddItem(item, 3)}
                    >
                      Add 3
                    </button>
                  </div>
                )}

                {getItemCountInCart(item.id) > 0 && (
                  <div className="notes-container">
                    <input
                      type="text"
                      placeholder="ðŸ’¡ Special instructions (optional)"
                      value={cart.find(cartItem => cartItem.id === item.id)?.notes || ''}
                      onChange={(e) => updateItemNotes(item.id, e.target.value)}
                      className="notes-input"
                    />
                  </div>
                )}
              </div>
            ))}
          </div>

          {filteredItems.length === 0 && (
            <div className="empty-menu">
              <div className="empty-icon">ðŸ”</div>
              <h3 className="empty-title">No items found</h3>
              <p className="empty-text">
                {searchTerm 
                  ? `No items found for "${searchTerm}". Try a different search term or category.`
                  : 'No items in this category. Please select a different category or check back later.'
                }
              </p>
              {searchTerm && (
                <button 
                  onClick={() => setSearchTerm('')}
                  className="clear-search-button-large"
                >
                  Clear Search
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Cart Popup */}
      {showCartPopup && (
        <div className="popup-overlay" onClick={() => setShowCartPopup(false)}>
          <div className="popup-content" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h2 className="popup-title">
                <span className="cart-icon">ðŸ›’</span>
                Your Order
                {cart.length > 0 && (
                  <span className="cart-item-count">({getCartTotalItems()} items)</span>
                )}
              </h2>
              <div className="popup-header-actions">
                {cart.length > 0 && (
                  <button 
                    className="clear-cart-button"
                    onClick={clearCart}
                    title="Clear entire cart"
                  >
                    ðŸ—‘ï¸ Clear All
                  </button>
                )}
                <button 
                  className="close-popup-button"
                  onClick={() => setShowCartPopup(false)}
                  title="Close cart"
                >
                  Ã—
                </button>
              </div>
            </div>
            
            <div className="popup-body">
              {cart.length === 0 ? (
                <div className="empty-cart">
                  <div className="empty-cart-icon">ðŸ›’</div>
                  <h3 className="empty-cart-title">Your cart is empty</h3>
                  <p className="empty-cart-text">Add some delicious items from the menu!</p>
                  <button 
                    className="continue-shopping-button"
                    onClick={() => setShowCartPopup(false)}
                  >
                    Continue Shopping
                  </button>
                </div>
              ) : (
                <>
                  <div className="cart-items">
                    {cart.map(item => (
                      <div key={item.id} className="cart-item">
                        <div className="cart-item-info">
                          <div className="cart-item-name">{item.name}</div>
                          <div className="cart-item-price">
                            {formatCurrency(item.price)} Ã— {item.quantity}
                          </div>
                          {item.notes && (
                            <div className="cart-item-notes">
                              ðŸ“ {item.notes}
                            </div>
                          )}
                        </div>
                        <div className="cart-quantity-controls">
                          <button 
                            className="quantity-btn-small"
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            title="Decrease quantity"
                          >
                            -
                          </button>
                          <span className="quantity-display-small">{item.quantity}</span>
                          <button 
                            className="quantity-btn-small"
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            title="Increase quantity"
                          >
                            +
                          </button>
                          <button 
                            className="remove-button"
                            onClick={() => removeFromCart(item.id)}
                            title="Remove item"
                          >
                            ðŸ—‘ï¸
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                  
                  <div className="cart-summary">
                    <div className="cart-total">
                      <span>Total:</span>
                      <span className="total-amount">{formatCurrency(getTotalAmount())}</span>
                    </div>
                    <div className="item-count">
                      {getCartTotalItems()} items â€¢ {cart.length} unique items
                    </div>
                  </div>
                  
                  <form className="order-form" onSubmit={handleSubmitOrder}>
                    <h3 className="form-title">
                      <span className="form-icon">ðŸ‘¤</span>
                      Customer Information
                    </h3>
                    
                    <div className="form-group">
                      <label className="form-label">Your Name *</label>
                      <input
                        type="text"
                        className="form-control"
                        value={customerInfo.name}
                        onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                        placeholder="Enter your name"
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Phone Number *</label>
                      <input
                        type="tel"
                        className="form-control"
                        value={customerInfo.phone}
                        onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                        placeholder="Enter your phone number"
                        required
                      />
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Table Number (if dining in)</label>
                      <input
                        type="text"
                        className="form-control"
                        value={customerInfo.table_number}
                        onChange={(e) => setCustomerInfo({...customerInfo, table_number: e.target.value})}
                        placeholder="e.g., Table 1, Table 5"
                      />
                    </div>
                    
                    <button 
                      type="submit" 
                      className={`submit-button ${orderLoading || cart.length === 0 ? 'disabled' : ''}`}
                      disabled={orderLoading || cart.length === 0}
                    >
                      {orderLoading ? (
                        <>
                          <div className="spinner"></div>
                          Placing Order...
                        </>
                      ) : (
                        <>
                          <span className="submit-icon">ðŸŽ¯</span>
                          Place Order - {formatCurrency(getTotalAmount())}
                        </>
                      )}
                    </button>

                    <div className="terms-text">
                      By placing order, you agree to our terms and conditions
                    </div>
                  </form>
                </>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomerOrder;
